import React from "react";
import useStyles from './styles';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import moment from 'moment';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';

// get post by destructuring props in the input; props => {post}
const Post = ({ post }) => {
    const classes = useStyles();
    return (
        <Card className={classes.card}>
            {/* need to add title from post; currently don't have a title field */}
            <CardMedia className={classes.media} image={post.selectedFile}/>
            {/* <div className={classes.overlay}>
                <Typography variant='h6'>{post.description}</Typography>
                <Typography variant='body2'>{moment(post.createdAt).fromNow()}</Typography>
            </div>
            <div className={classes.overlay2}>
                <Button style={{color: 'white'}} size='small' onClick={() => {}}>
                    <MoreHorizIcon fontSize='medium'/>
                </Button>
            </div> */}
            <div className={classes.details}>
                <Typography variant='body2' color='textSecondary'>{post.tags.map((tag) => (`#${tag}`))}</Typography>
            </div>
            <CardContent>
                <Typography className={classes.title} variant='h5' gutterBottom>Food: {post.description}</Typography>
                <Typography className={classes.title} variant='h5' gutterBottom>Location: {post.location}</Typography>
                <Typography className={classes.title} variant='h5' gutterBottom>Posted by: {post.name} </Typography>
            </CardContent>
            <CardActions className={classes.cardActions}>
                <Button size='small' color='primary' onClick={()=> {}}>
                    <ThumbUpAltIcon fontSize='small' />
                    {post.likeCount} Like
                </Button>
                <Button size='small' color='secondary' onClick={()=> {}}>
                    <DeleteIcon fontSize='small' />
                    Delete
                </Button>
            </CardActions>
        </Card>
    )
}

export default Post;
