// a reducer is a function that accepts the state and the action; return either action or state changed by the action
// state must always be equal to something
// state = [] because posts start out as an empty array
export default (posts = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return posts;
        case 'CREATE':
            return posts;
        default:
            return posts;
    }
}
