import React, { useEffect, useState } from "react";
import { AppBar, Grow, Button, Typography, Toolbar, Card, CardContent, CardMedia, CssBaseline, Grid, Container } from '@material-ui/core';
import useStyles from './styles';
import Posts from './components/Posts/Posts.js';
import Form from './components/Form/Form.js';

import { useDispatch } from 'react-redux';
import { getPosts } from './actions/posts';

import FastfoodIcon from '@material-ui/icons/Fastfood';

// have to keep track of the current post id in APP.JS bc we have to share the state of current id between the post and form,
// and our app is the only parent component that is parent to both post and form

const App = () => {
    const [currentId, setCurrentId] = useState(0);
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        // dispatch getPosts action, which ensures that for every change, we're going to get new post(s)
        dispatch(getPosts());
    }, [currentId, dispatch]);

    return (
        <Container maxwidth="lg">
            <AppBar position='sticky' style={{marginBottom: '30px'}}>
                <Toolbar className={classes.toolbar}>
                <div className={classes.logo}>
                    <FastfoodIcon className={classes.icon}/>
                    <Typography variant='h4'>
                        FoodFinder NU
                    </Typography>
                </div>
                <div>
                    <Button variant='contained'>Log in</Button>
                </div>
                </Toolbar>
            </AppBar>
            <Grow in>
                <Container>
                    <Grid container justifyContent='space-between' alignitems='stretch' spacing={3}>
                        <Grid item xs={12} sm={7}>
                            <Posts setCurrentId={setCurrentId}/>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Form currentId={currentId} setCurrentId={setCurrentId}/>
                        </Grid>
                    </Grid>
                </Container>
            </Grow>

        </Container>
    )
};

export default App;





