import React, { useState } from "react";
import { TextField, Button, Typography, Paper } from "@material-ui/core";
import FileBase from 'react-file-base64';
import useStyles from './styles';

const Form = () => {
    const [postData, setPostData] = useState({
        description: '', location: '', creator: '', tags: '', selectedFile: ''
    });
    const classes = useStyles();
    const handleSubmit = () => {

    }

    return (
        <Paper className={classes.paper}>
            <form autoComplete='off' noValidate className={classes.form} onSubmit={handleSubmit}>
                <Typography variant='h6'>
                    Creating a Memory
                </Typography>
                <TextField name='Description of food' variant='outlined' label='Description' fullWidth value={postData.description} onChange={(e) => setPostData({ ...postData, description: e.target.value })}/>
                <TextField name='Location' variant='outlined' label='Location' fullWidth value={postData.location} onChange={(e) => setPostData({ ...postData, location: e.target.value })}/>
                <TextField name='Name' variant='outlined' label='Name' fullWidth value={postData.name} onChange={(e) => setPostData({ ...postData, name: e.target.value })}/>
                <TextField name='tags' variant='outlined' label='Tags' fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value })}/>
                <div className={classes.fileInput}>
                    <FileBase
                        type='file'
                        multiple={false}
                        onDone={({base64}) => setPostData({ ...postData, selectedFile: base64 })}
                    />
                </div>
            </form>
        </Paper>
    )
}

export default Form;
