// a reducer handles the action and decides how to update the state
// state must always be equal to something
// state = [] because posts start out as an empty array
export default (posts = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return action.payload;
        case 'CREATE':
            // we have to send over an array of posts; first we spread all the posts, and then we add a new post
            return [...posts, action.payload];
        default:
            return posts;
    }
}
